                          <div class="login-form-1">
                            <form id="login-form" class="text-left"  @submit.prevent="AuthReq()">
                              <div class="main-login-form">
                                <div class="login-group">
                                  <div class="form-group">
                                    <input
                                      name="username"
                                      type="text"
                                      class="form-control"
                                      id="lg_username"
                                      placeholder="Имя"
                                      required
                                      v-model="username"
                                    />
                                  </div>
                                  <div class="form-group">
                                    <input
                                      name="telephone"
                                      type="text"
                                      class="form-control"
                                      id="lg_telephone"
                                      placeholder="Контактный телефон"
                                      required
                                      v-model="telephone"
                                    />
                                  </div>
                                  <div class="form-group">
                                    <input
                                      name="email"
                                      type="text"
                                      class="form-control"
                                      id="lg_email"
                                      placeholder="E-mail"
                                      required
                                      v-model="email"
                                    />
                                  </div>
                                    <small v-if="warning">Некорректный email адрес</small>
                                  <div class="form-group">
                                    <input
                                      name="password"
                                      type="password"
                                      class="form-control"
                                      id="lg_password"
                                      placeholder="Пароль"
                                      required
                                      v-model="password"
                                    />
                                  </div>
                                  <div
                                    class="form-group form-group-remember no-margin no-padding login-group-checkbox"
                                  >
                                    <div class="theme-checkbox" >
                                      <input
                                        type="checkbox"
                                        v-model="check_box"
                                        id="check-box-1"
                                        name="radio-btn"
                                      />
                                      <label for="check-box-1" style="white-space: pre-wrap"><small>Согласие на обработку <u>персональных данных</u></small></label>
                                    </div>
                                  </div>
                                </div>
                                <button type="submit" class="login-button">
                                  <font-awesome-icon :icon="['fas', 'chevron-right']"/>
                                </button>
                              </div>
                              <div class="etc-login-form text-center text-main">
                                <p class="mar-top-15 no-margin-bottom">
                                  <nuxt-link to="login">Назад</nuxt-link>
                                </p>
                              </div>
                            </form>
                          </div>
                        </b-col>
                      </b-row>
                    </div>

                </div>
              </div>
            </div>
        </b-row>
      </div>
    </div>
  </div> 
  
</template> 

<script>
import Api from "~/utils/api";

export default {
  layout: 'account',
  data: () => ({
    email: null,
    password: null,
    username: null,
    telephone: null,
    check_box: false,
    warning: false,
  }),
  mounted(){

  },

  methods: {

    event_checkbox(){

    },

    AuthReq() {
      if(this.check_box){
        if(this.validateEmail(this.email)){
        Api.getInstance()
            .auth.registration(this.username, this.telephone, this.email, this.password)
            .then((response) => {
                this.$bvToast.toast("Регистрация прошла успешно.", {
                title: `Сообщение:`,
                variant: "success",
                solid: true,
                });
                setTimeout(()=>{this.$router.push('/account/login')}, 1500)
            })
            .catch((error) => {
            console.log(error.response)
            if(error.response.status == 401){
                this.$bvToast.toast("Данный пользователь уже существует в системе.", {
                    title: `Ошибка регистрации`,
                    variant: "danger",
                    solid: true,
                });
            }else{
                this.$bvToast.toast("Сервер не отвечает", {
                    title: `Ошибка регистрации`,
                    variant: "danger",
                    solid: true,
                });
            }
            });
        this.warning = false
        } 
      }else{
        this.$bvToast.toast("Пожалуйста согласитесь на обработку персональных данных.", {
            title: `Ошибка валидации`,
            variant: "danger",
            solid: true,
        });
      }
    },
    // ФУНКЦИЯ ПРОВЕРКИ НА КОРРЕКТНОСТЬ ВВЕДЕНОЙ ПОЧТЫ
    validateEmail(value){
      if (/^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/.test(value)) {
        return true
      }else{
        this.warning = true
        return false
      } 
    },
  }
}
</script>
